import { PrismaClient } from '@prisma/client';
const prisma = new PrismaClient();

//password = 123456

async function main() {
  await prisma.user.upsert({
    where: { email: 'bruna.platt@hotmail.com' },
    update: {},
    create: {
      fullName: 'Bruna Silveira Platt',
      email: 'bruna.platt@hotmail.com',
      password: '123456',
      roles: 'administrator',
    },
  });
}
main()
  .then(async () => {
    await prisma.$disconnect();
  })
  .catch(async (e) => {
    console.error(e);
    await prisma.$disconnect();
    process.exit(1);
  });